function showCategory(type) {
    if(!currentCity) return;
    let title = "", content = "";

    switch(type) {
        case 'turismo':
            title = "Pontos TurÃ­sticos"; 
            content = formatList(currentCity.pontos_turisticos); 
            break;
        case 'gastronomia': 
            title = "Gastronomia TÃ­pica"; 
            content = formatList(currentCity.gastronomia); 
            break;
        case 'religiao': 
            title = "Cultura & ReligiÃ£o"; 
            content = formatList(currentCity.religiao); 
            break;
        case 'curiosidades': 
            title = "Curiosidades"; 
            content = formatList(currentCity.curiosidades); 
            break;
        case 'eventos':
            title = "CalendÃ¡rio & EstaÃ§Ãµes";
            content = currentCity.eventos_estacoes ? formatList(currentCity.eventos_estacoes) : `<p><strong>EstaÃ§Ãµes:</strong> ${currentCity.estacoes}</p><p><strong>Eventos:</strong> ${currentCity.eventos}</p>`;
            break;
        case 'geral':
            title = "InformaÃ§Ãµes Gerais";
            content = currentCity.info_gerais ? formatList(currentCity.info_gerais) : `<p><strong>Moeda:</strong> ${currentCity.moeda}</p>`;
            break;
        case 'antes':
            title = "Antes de Ir (Dicas de Ouro)";
            content = currentCity.antes_de_ir ? formatList(currentCity.antes_de_ir) : `<p><strong>antes:</strong> ${currentCity.antes}</p>`;
            break;
        case 'numeros':
            title = "NÃºmeros Importantes";
            content = currentCity.numeros ? formatList(currentCity.numeros) : "<p>NÃºmeros nÃ£o cadastrados para esta cidade.</p>";
            break;
        case 'riscos':
            title = "âš ï¸ Riscos & Erros Comuns";
            content = currentCity.riscos ? formatList(currentCity.riscos) : "<p>Dados de riscos ainda nÃ£o cadastrados para esta cidade.</p>";
            break;
    }

    detailTitle.innerText = title;
    detailContent.innerHTML = content;
    modalMenu.classList.add('hidden');
    modalDetails.classList.remove('hidden');
}

